import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import UserTable from './components/UserTable';
import SearchBar from './components/SearchBar';
import ThemeToggle from './components/ThemeToggle';
import ColumnVisibilityToggle from './components/ColumnVisibilityToggle';
import useDebounce from './hooks/useDebounce';
import './styles/App.css';

function App() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: null, direction: null });
  const [theme, setTheme] = useState('dark');
  const [visibleColumns, setVisibleColumns] = useState({
    name: true,
    email: true,
    company: true,
    phone: true,
    website: true,
    address: true,
  });
  const [currentPage, setCurrentPage] = useState(1);
  const rowsPerPage = 5;
  const searchInputRef = useRef(null);

  // Fetch users
  // Fetch users function
const fetchUsers = useCallback(() => {
  setLoading(true);
  fetch('https://jsonplaceholder.typicode.com/users')
    .then((res) => res.json())
    .then((data) => {
      setUsers(data);
      setLoading(false);
    })
    .catch((err) => {
      setError(err.message);
      setLoading(false);
    });
}, []);

// Run once on mount
useEffect(() => {
  fetchUsers();
}, [fetchUsers]);


  // Keyboard shortcut: "/" focuses search
  useEffect(() => {
    const handler = (e) => {
      if (e.key === '/') {
        e.preventDefault();
        searchInputRef.current?.focus();
      }
    };
    window.addEventListener('keydown', handler);
    return () => window.removeEventListener('keydown', handler);
  }, []);

  const debouncedSearch = useDebounce(search, 300);

  const filteredUsers = useMemo(() => {
    return users.filter((user) =>
      user.name.toLowerCase().includes(debouncedSearch.toLowerCase()) ||
      user.email.toLowerCase().includes(debouncedSearch.toLowerCase())
    );
  }, [users, debouncedSearch]);

  const sortedUsers = useMemo(() => {
    if (!sortConfig.key) return filteredUsers;
    return [...filteredUsers].sort((a, b) => {
      const aVal = sortConfig.key === 'company' ? a.company.name : a[sortConfig.key];
      const bVal = sortConfig.key === 'company' ? b.company.name : b[sortConfig.key];
      if (aVal < bVal) return sortConfig.direction === 'asc' ? -1 : 1;
      if (aVal > bVal) return sortConfig.direction === 'asc' ? 1 : -1;
      return 0;
    });
  }, [filteredUsers, sortConfig]);

  const paginatedUsers = useMemo(() => {
    const start = (currentPage - 1) * rowsPerPage;
    return sortedUsers.slice(start, start + rowsPerPage);
  }, [sortedUsers, currentPage]);

  const totalPages = Math.ceil(sortedUsers.length / rowsPerPage);

  const handleSort = useCallback((key) => {
    setSortConfig((prev) => {
      if (prev.key === key) {
        return { key, direction: prev.direction === 'asc' ? 'desc' : 'asc' };
      }
      return { key, direction: 'asc' };
    });
  }, []);

  if (loading) return <p className="status">Loading users...</p>;
  if (error) return <p className="status error">Error: {error}</p>;

  return (
    <div className={`app ${theme}`}>
      <header className="header">
        <h1>User Management Dashboard</h1>
        <p>Total Users: {users.length}</p>
        <ThemeToggle theme={theme} setTheme={setTheme} />
      </header>
      <div className="controls">
        <SearchBar search={search} setSearch={setSearch} inputRef={searchInputRef} />
        <ColumnVisibilityToggle visibleColumns={visibleColumns} setVisibleColumns={setVisibleColumns} />
      </div>
      <UserTable
  users={paginatedUsers}
  sortConfig={sortConfig}
  onSort={handleSort}
  visibleColumns={visibleColumns}
  onResetData={fetchUsers}   
/>

      <div className="pagination">
        <button onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))} disabled={currentPage === 1}>Prev</button>
        <span>Page {currentPage} of {totalPages}</span>
        <button onClick={() => setCurrentPage((p) => Math.min(p + 1, totalPages))} disabled={currentPage === totalPages}>Next</button>
      </div>
    </div>
  );
}

export default App;
