import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import UserTable from './components/UserTable';
import SearchBar from './components/SearchBar';
import ThemeToggle from './components/ThemeToggle';
import ColumnVisibilityToggle from './components/ColumnVisibilityToggle';
import useDebounce from './hooks/useDebounce';
import './styles/App.css';
import { Dashboard, People, ArrowBackIos, ArrowForwardIos } from '@mui/icons-material';


function App() {
  const [users, setUsers] = useState([]);
  const [originalUsers, setOriginalUsers] = useState([]); // store first fetch order
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: null, direction: null });
  const [theme, setTheme] = useState('light');
  const [visibleColumns, setVisibleColumns] = useState({
    name: true,
    email: true,
    company: true,
    phone: true,
    website: true,
    address: true,
  });
  const [currentPage, setCurrentPage] = useState(1);
  const [rowsPerPage, setRowsPerPage] = useState(5);



  const searchInputRef = useRef(null);

  // Fetch users
  const fetchUsers = useCallback(() => {
    setLoading(true);
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((res) => res.json())
      .then((data) => {
        setUsers(data);
        setOriginalUsers(data); // preserve first-fetch order
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  // Keyboard shortcut: "/" focuses search
  useEffect(() => {
    const handler = (e) => {
      if (e.key === '/') {
        e.preventDefault();
        searchInputRef.current?.focus();
      }
    };
    window.addEventListener('keydown', handler);
    return () => window.removeEventListener('keydown', handler);
  }, []);

  const debouncedSearch = useDebounce(search, 300);

  // Always filter against originalUsers (not already sorted users)
  const filteredUsers = useMemo(() => {
    return originalUsers.filter((user) =>
      user.name.toLowerCase().includes(debouncedSearch.toLowerCase()) ||
      user.email.toLowerCase().includes(debouncedSearch.toLowerCase())
    );
  }, [originalUsers, debouncedSearch]);

  // Sorting
  const sortedUsers = useMemo(() => {
    if (!sortConfig.key || sortConfig.direction === "reset") {
      return filteredUsers; // back to filtered original order
    }

    return [...filteredUsers].sort((a, b) => {
      const aVal = sortConfig.key === 'company' ? a.company.name : a[sortConfig.key];
      const bVal = sortConfig.key === 'company' ? b.company.name : b[sortConfig.key];
      if (aVal < bVal) return sortConfig.direction === 'asc' ? -1 : 1;
      if (aVal > bVal) return sortConfig.direction === 'asc' ? 1 : -1;
      return 0;
    });
  }, [filteredUsers, sortConfig]);

  // Pagination
  const paginatedUsers = useMemo(() => {
    const start = (currentPage - 1) * rowsPerPage;
    return sortedUsers.slice(start, start + rowsPerPage);
  }, [sortedUsers, currentPage, rowsPerPage]);

  const totalPages = Math.ceil(sortedUsers.length / rowsPerPage);

  // Sort handler
  const handleSort = useCallback((key, direction) => {
    if (direction === "reset") {
      setSortConfig({ key: null, direction: "reset" }); // reset mode
    } else {
      setSortConfig({ key, direction });
    }
  }, []);

  if (loading) return <p className="status">Loading users...</p>;
  if (error) return <p className="status error">Error: {error}</p>;

  return (
    <div className={`app ${theme}`}>
      <header className="header">
        <h1>
          <Dashboard className="dashboard-icon" />
          User Management Dashboard</h1>
        <p>Total Users: {originalUsers.length}</p>
        <ThemeToggle theme={theme} setTheme={setTheme} />
      </header>

      <div className="controls">
        <SearchBar search={search} setSearch={setSearch} inputRef={searchInputRef} />
        <ColumnVisibilityToggle visibleColumns={visibleColumns} setVisibleColumns={setVisibleColumns} />
      </div>

      <UserTable
        users={paginatedUsers}
        sortConfig={sortConfig}
        onSort={handleSort}
        visibleColumns={visibleColumns}
      />

      <div className="pagination">
        <button onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))} disabled={currentPage === 1}>Prev</button>
        <span className='page-info'>Page {currentPage} of {totalPages}</span>
        <button onClick={() => setCurrentPage((p) => Math.min(p + 1, totalPages))} disabled={currentPage === totalPages}>Next</button>
        <div className='rows-per-page'>
        <label>
    Users per page:{" "}
    <select
  value={rowsPerPage}
  onChange={(e) => {
    const newRows = Number(e.target.value);
    setRowsPerPage(newRows);

    // recalc total pages and clamp currentPage
    const newTotalPages = Math.ceil(sortedUsers.length / newRows);
    setCurrentPage((prev) => Math.min(prev, newTotalPages));
  }}
>
  <option value={5}>5</option>
  <option value={10}>10</option>
  <option value={20}>20</option>
</select>

  </label>
  </div>
      </div>
    </div>
  );
}

export default App;
